    protected override async Task OnInitializedAsync()
    {

        //https://api.n.exchange/en/api/v1/pair/
        //"sample-data/weather.json"
        pairs = await Http.GetFromJsonAsync<Pairs[]>("https://api.n.exchange/en/api/v1/pair/");
